import React from "react";
import logo from "./title.png";
import "./App.css";
import { FirebaseContext } from "../firebase";
//import { Component } from './colour-picker';
import { SketchPicker } from "react-color";

class Component extends React.Component {
  state = {
    background: "#fff", //default colour. this will be the colour from css file
    //displayColorPicker: false,
  };
  handleClick = () => {
    this.setState({ displayColorPicker: !this.state.displayColorPicker });
  };

  handleClose = () => {
    this.setState({ displayColorPicker: false });
  };

  handleChangeComplete = (color) => {
    this.setState({ background: color.hex }); //update the colour when done picking
  };
  handleChange(color, event) {
    // update colour whenever change made (eg when you drag to select a colour)
    this.setState({ background: color.hex });
  }
  render() {
    return (
     // <div>
     //   <button onClick={this.handleClick}>Pick Color</button>
     //   {this.state.displayColorPicker ? (
    //      <div style={popover}>
     //       <div style={cover} onClick={this.handleClose} />
    //        <SketchPicker />
     //     </div>
    //    ) : null}
    //  </div>

        <SketchPicker
        color = { this.state.background }
        onChange={ this.handleChange }
        onChangeComplete={ this.handleChangeComplete }
         />
    );
  }
}

const App = () => (
  <div className="App">
    <header className="App-header">
      [// Draw the logo]
      <img src={logo} className="App-logo" alt="logo" />
      [// Make connection w/ firebase via the context]
      <FirebaseContext.Consumer>
        {(firebase) => {
          return (
            <h2 className="App-title">Connection to firebase succesful!</h2>
          );
        }}
      </FirebaseContext.Consumer>
    </header>
    return {Component.render};
  </div>
);

export default App;
